import React from 'react';

const LinkControls = ({ promptForLink, showURLInput, removeLink, onLinkInputKeyDown, confirmLink, urlRef, urlValue, setUrlValue }) => {
    return (
        <>
            <button
                className="btn small ml-0"
                onClick={e => promptForLink(e)}
            >
                Add Link
        </button>
            <button className="btn small" onClick={e => removeLink(e)}>
                Remove Link
        </button>
            {
                showURLInput && (
                    <>
                        <input className="input" type="text" placeholder="Enter URL" ref={urlRef}
                            onChange={e => setUrlValue(e.target.value)}
                            type="text"
                            value={urlValue}
                            onKeyDown={e => onLinkInputKeyDown(e)} />
                        <button className="btn primary small" onClick={e => confirmLink(e)}>
                            Confirm
            </button>
                    </>
                )
            }
        </>
    )
}

export default LinkControls;
